//present in the profile page
import React, { useState } from "react";
import { useNavigate } from "react-router-dom"; // Import for navigation

const BuyCard = ({ product }) => {
  const [buttonState, setButtonState] = useState("Accept");
  const [showModal, setShowModal] = useState(false);
  const [sellerDetails, setSellerDetails] = useState(null); // State to store seller details
  const navigate = useNavigate();
  const userEmail = localStorage.getItem("email"); // Get the current user's email
  const [bidAmount, setBidAmount] = useState(""); 

  // Prevent rendering the card if conditions are not met
  if (!product.buyerStudentID) {
    return null;
  }

  const handleAcceptClick = async () => {
    try {
      // Send data to the backend
      const response = await fetch("http://localhost:8000/api/acceptProduct", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ productId: product._id }), // Send the product ID to the backend
      });

      if (response.ok) {
        setButtonState("View Details"); // Change button state
      } else {
        console.error("Failed to accept product.");
      }
    } catch (error) {
      console.error("Error accepting product:", error);
    }
  };

   // Function to handle Bid action
   const handleBidClick = async () => {
    try {
      const response = await fetch("http://localhost:8000/api/placeBid", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          productId: product._id,
          buyerStudentID: userEmail,
          bidAmount: bidAmount,
        }),
      });

      if (response.ok) {
        alert("Bid placed successfully!");
      } else {
        alert("Place a bid higher than the current bid and check your wallet amount.");
      }
    } catch (error) {
      console.error("Error placing bid:", error);
    }
  };


  const handleViewDetailsClick = async () => {
    try {
      // Fetch seller details from the backend
      const response = await fetch(
        `http://localhost:8000/api/sellerDetails?productId=${product._id}`
      );
      const data = await response.json();
      setSellerDetails(data); // Store seller details in state
      setShowModal(true); // Show the modal
    } catch (error) {
      console.error("Error fetching seller details:", error);
    }
  };

  const closeModal = () => {
    setShowModal(false);
    navigate("/profilepage"); // Redirect to profile page
  };
//user sales history
  if(product.sellerStudentID === userEmail)
  return (
    <div className="bg-white rounded-lg shadow-md overflow-hidden p-4">
      <h5> this is sales history</h5>
      <img
        src={`http://localhost:8000${product.prodImage}`}
        alt={product.productName}
        className="w-full h-48 object-cover"
      />
      <div className="p-4">
        <h2 className="text-xl font-semibold text-gray-800">
          {product.productName}
        </h2>
        <p className="text-gray-500">Type: {product.productType}</p>
        <p className="text-gray-700 font-semibold">Price: ${product.price}</p>
        <div className="flex justify-center space-x-4 mt-4">
          {product.bid ? (
            <button
              onClick={handleAcceptClick}
              className="bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700"
            >
              Accept
            </button>
          ) : (
            <button
              onClick={handleViewDetailsClick}
              className="bg-green-600 text-white px-4 py-2 rounded-md hover:bg-green-700"
            >
              View Details
            </button>
          )}
        </div>
      </div>

      {/* Modal for seller details */}
      {showModal && (
        <div className="fixed inset-0 flex items-center justify-center z-50 bg-black bg-opacity-50">
          <div
            className="bg-white rounded-lg p-6 shadow-lg max-w-md w-full"
            onClick={(e) => e.stopPropagation()}
          >
            <h3 className="text-2xl font-semibold text-gray-800 mb-4">
              Seller Details
            </h3>
            {sellerDetails ? (
              <>
                <p className="text-gray-700">
                  <strong>Name:</strong> {sellerDetails.name}
                </p>
                <p className="text-gray-700">
                  <strong>Email:</strong> {sellerDetails.email}
                </p>
                <p className="text-gray-700">
                  <strong>Phone:</strong> {sellerDetails.contactNo}
                </p>
                <p className="text-gray-700">
                  <strong>Address:</strong> {sellerDetails.address}
                </p>
              </>
            ) : (
              <p>Loading...</p>
            )}
            <button
              onClick={closeModal}
              className="mt-6 w-full bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700"
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );

  //Purchase history
  if(product.buyerStudentID === userEmail)
    return (
  
      <div className="bg-white rounded-lg shadow-md overflow-hidden p-4">
        <h5> this is purchase history</h5>
        <img
          src={`http://localhost:8000${product.prodImage}`}
          alt={product.productName}
          className="w-full h-48 object-cover"
        />
        <div className="p-4">
          <h2 className="text-xl font-semibold text-gray-800">
            {product.productName}
          </h2>
          <p className="text-gray-500">Type: {product.productType}</p>
          <p className="text-gray-700 font-semibold">Price: ${product.price}</p>
          <div className="flex justify-center space-x-4 mt-4">
            {product.bid ? (
              <div>
              <button
                onClick={handleBidClick}
                className="bg-green-600 text-white px-4 py-2 rounded-md hover:bg-green-700"
              >
                Bid
              </button>
              <input
                type="number"
                value={bidAmount}
                onChange={(e) => setBidAmount(e.target.value)}
                placeholder="Enter bid"
                className="rounded-md border text-black ml-2"
              />
            </div>
            ) : (
              <div>
              <button
                onClick={handleViewDetailsClick}
                className="bg-green-600 text-white px-4 py-2 rounded-md hover:bg-green-700"
              >
                Reject Product
              </button>

              <button
                onClick={handleViewDetailsClick}
                className="bg-green-600 text-white px-4 py-2 rounded-md hover:bg-green-700"
              >
                  Accept Product
                  </button>
                  </div>
            )}
          </div>
        </div>
  
        {/* Modal for seller details */}
        {showModal && (
          <div className="fixed inset-0 flex items-center justify-center z-50 bg-black bg-opacity-50">
            <div
              className="bg-white rounded-lg p-6 shadow-lg max-w-md w-full"
              onClick={(e) => e.stopPropagation()}
            >
              <h3 className="text-2xl font-semibold text-gray-800 mb-4">
                Seller Details
              </h3>
              {sellerDetails ? (
                <>
                  <p className="text-gray-700">
                    <strong>Name:</strong> {sellerDetails.name}
                  </p>
                  <p className="text-gray-700">
                    <strong>Email:</strong> {sellerDetails.email}
                  </p>
                  <p className="text-gray-700">
                    <strong>Phone:</strong> {sellerDetails.contactNo}
                  </p>
                  <p className="text-gray-700">
                    <strong>Address:</strong> {sellerDetails.address}
                  </p>
                </>
              ) : (
                <p>Loading...</p>
              )}
              <button
                onClick={closeModal}
                className="mt-6 w-full bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700"
              >
                Close
              </button>
            </div>
          </div>
        )}
      </div>
    );


};

export default BuyCard;
