import React, { useState, useEffect } from "react";
import Navbar from "../components/navbar";
import Footer from "../components/footer";
import SellList from "../components/sellList";
import { useNavigate } from "react-router-dom";

const Sell = () => {
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({
    image: "",
    name: "",
    type: "",
    description: "",
    price: "",
    bid: false, // Initialize bid as a boolean
  });
  const navigate = useNavigate();
  const [refreshList, setRefreshList] = useState(false); // Trigger for refreshing SellList

  const userEmail = localStorage.getItem("email");

  // Redirect to login if no user email found
  useEffect(() => {
    if (!userEmail) {
      alert("Please log in to continue");
      navigate("/login");
    }
  }, [userEmail, navigate]);

  const handleToggleForm = () => {
    setShowForm(!showForm);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleBidChange = (value) => {
    setFormData({ ...formData, bid: value === true }); // Set bid as true for "yes" and false for "no"
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formDataToSend = new FormData();
    formDataToSend.append("image", formData.image);
    formDataToSend.append("productName", formData.name);
    formDataToSend.append("productType", formData.type);
    formDataToSend.append("productDesc", formData.description);
    formDataToSend.append("price", formData.price);
    formDataToSend.append("bid", formData.bid); // Boolean bid value

    // Default or required values
    formDataToSend.append("sellerStudentID", userEmail);
    formDataToSend.append("productID", `PROD_${Date.now()}`);

    try {
      const response = await fetch("http://localhost:8000/api/postProducts", {
        method: "POST",
        body: formDataToSend,
      });
      if (response.ok) {
        alert("Product listed successfully!");
        setFormData({
          image: "",
          name: "",
          type: "",
          description: "",
          price: "",
          bid: false,
        });
        setShowForm(false);
        setRefreshList(!refreshList);
      } else {
        console.log("Failed to list product.");
      }
    } catch (error) {
      console.error("Error listing product:", error);
      alert("An error occurred. Please try again.");
    }
  };

  const handleOutsideClick = (e) => {
    if (e.target.id === "form-overlay") {
      setShowForm(false);
    }
  };

  return (
    <div className="flex flex-col min-h-screen">
      <Navbar />
      <div
        className={`${
          showForm ? "blur-sm" : ""
        } flex-grow p-8 bg-gray-900 pt-24 text-white transition duration-200`}
      >
        <div className="flex justify-center mt-10">
          {!showForm && (
            <button
              onClick={handleToggleForm}
              className="text-3xl bg-green-500 p-3 rounded-md hover:bg-green-700 transition duration-200"
            >
              Sell Product
            </button>
          )}
        </div>

        <SellList refresh={refreshList} />
      </div>

      {showForm && (
        <div
          id="form-overlay"
          onClick={handleOutsideClick}
          className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-75 z-50"
        >
          <div
            onClick={(e) => e.stopPropagation()}
            className="p-10 bg-white rounded-lg max-w-md w-full mx-auto shadow-lg"
          >
            <div className="text-center text-2xl mb-4">
              Upload the Details of Product
            </div>
            <form onSubmit={handleSubmit} className="space-y-4">
              <div>
                <input
                  type="file"
                  name="image"
                  accept="image/*"
                  onChange={(e) =>
                    setFormData({ ...formData, image: e.target.files[0] })
                  }
                  required
                  className="block py-2 px-3 w-full text-sm text-gray-900 border border-gray-300 rounded-md cursor-pointer bg-gray-50 focus:outline-none"
                />
              </div>

              <div>
                <input
                  type="text"
                  name="name"
                  placeholder="Enter the product Name"
                  value={formData.name}
                  onChange={handleChange}
                  required
                  className="w-full px-3 py-2 mt-2 border border-gray-600 rounded-md text-black placeholder-gray-300"
                />
              </div>

              <div>
                <input
                  type="text"
                  name="type"
                  placeholder="Enter the Type"
                  value={formData.type}
                  onChange={handleChange}
                  required
                  className="w-full px-3 py-2 mt-2 border border-gray-600 rounded-md text-black placeholder-gray-300"
                />
              </div>

              <div>
                <textarea
                  name="description"
                  placeholder="Enter the Description"
                  value={formData.description}
                  onChange={handleChange}
                  required
                  className="w-full px-3 py-2 mt-2 border border-gray-600 rounded-md text-black placeholder-gray-300"
                />
              </div>

              <div>
                <input
                  type="number"
                  min="0"
                  name="price"
                  placeholder="Selling Price"
                  value={formData.price}
                  onChange={handleChange}
                  required
                  className="w-full px-3 py-2 mt-2 border border-gray-600 rounded-md text-black placeholder-gray-300"
                />
              </div>

              <div className="flex items-center space-x-4">
                <label htmlFor="bid" className="text-sm">
                  Want to Bid
                </label>
                <div className="flex items-center space-x-2">
                  <input
                    type="radio"
                    id="bid-yes"
                    name="bid"
                    value="yes"
                    checked={formData.bid === true}
                    onChange={() => handleBidChange("yes")}
                    className="h-4 w-4 text-blue-600 border-gray-300"
                  />
                  <label htmlFor="bid-yes" className="text-sm">Yes</label>
                  <input
                    type="radio"
                    id="bid-no"
                    name="bid"
                    value="no"
                    checked={formData.bid === false}
                    onChange={() => handleBidChange("no")}
                    className="h-4 w-4 text-blue-600 border-gray-300"
                  />
                  <label htmlFor="bid-no" className="text-sm">No</label>
                </div>
              </div>

              <button
                type="submit"
                className="w-full bg-green-500 py-2 px-4 rounded-md text-black hover:bg-green-700 transition duration-200"
              >
                Submit
              </button>
            </form>
          </div>
        </div>
      )}
      <Footer />
    </div>
  );
};

export default Sell;
