import React, { useEffect, useState } from "react";
import BuyCard from "./buyCard";

const BuyList = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch("http://localhost:8000/api/products");
        const data = await response.json();
        setProducts(data);
      } catch (error) {
        console.error("Failed to fetch products:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchProducts();
  }, []);

  if (loading)
    return <p className="text-center text-white">Loading products...</p>;

  return (
    <div className="p-4 lg:p-8 bg-gray-800 rounded-2xl">
      <h1 className="text-3xl font-bold text-green-500 text-center mb-8">
        Product List
      </h1>
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
        {products.map((product) => (
          <BuyCard key={product._id} product={product} />
        ))}
      </div>
    </div>
  );
};

export default BuyList;
