import React, { useState, useEffect } from "react";
import Navbar from "../components/navbar";
import Footer from "../components/footer";
import SellList from "../components/sellList";
import { useNavigate } from "react-router-dom";

const Sell = () => {
  const [showForm, setShowForm] = useState(false);
  const [bid, setBid] = useState(false);
  const [formData, setFormData] = useState({
    image: "",
    name: "",
    type: "",
    description: "",
    price: "",
    bid: "",
  });
  const navigate = useNavigate();
  const [refreshList, setRefreshList] = useState(false); // Trigger for refreshing SellList

  const userEmail = localStorage.getItem("email");

  // Redirect to login if no user email found
  useEffect(() => {
    if (!userEmail) {
      alert("Please log in to continue");
      navigate("/login");
    }
  }, [userEmail, navigate]);

  const handleToggleForm = () => {
    setShowForm(!showForm);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Form submission logic: Send data to API
    const formDataToSend = new FormData();
    formDataToSend.append("image", formData.image); // Matches `prodImage` in the schema
    formDataToSend.append("productName", formData.name); // Matches `productName` in the schema
    formDataToSend.append("productType", formData.type); // Matches `productType` in the schema
    formDataToSend.append("productDesc", formData.description); // Matches `productDesc` in the schema
    formDataToSend.append("price", formData.price); // `price` matches schema
    formDataToSend.append("bid", false); // Set bid to false or true as needed

    // Default or required values
    formDataToSend.append("sellerStudentID", userEmail); // Replace with actual seller ID if needed
    formDataToSend.append("productID", `PROD_${Date.now()}`); // Unique product ID

    try {
      const response = await fetch("http://localhost:8000/api/postProducts", {
        method: "POST",
        body: formDataToSend,
      });
      if (response.ok) {
        alert("Product listed successfully!");
        setFormData({
          image: "",
          name: "",
          type: "",
          description: "",
          price: "",
        });
        setShowForm(false);
        setBid(false);
        setRefreshList(!refreshList);
      } else {
        console.log("Failed to list product.");
      }
    } catch (error) {
      console.error("Error listing product:", error);
      alert("An error occurred. Please try again.");
    }
  };

  const handleOutsideClick = (e) => {
    if (e.target.id === "form-overlay") {
      setShowForm(false);
    }
  };
  return (
    <div className="flex flex-col min-h-screen">
      <Navbar />
      <div
        className={`${
          showForm ? "blur-sm" : ""
        } mt-24 bg-gray-900 flex-grow p-8 text-white  h-screen transition duration-200`}
      >
        <div className="mt-10 p-10 flex justify-center">
          {!showForm && (
            <button
              onClick={handleToggleForm}
              className="text-3xl bg-green-500 rounded p-3 rounded-md hover:bg-green-700 transition duration-200"
            >
              Sell Product
            </button>
          )}
        </div>

        {/* Display list of products and trigger re-render on refreshList change */}
        <SellList refresh={refreshList} />
      </div>

      {/* Conditionally render the form overlay */}
      {showForm && (
        <div
          id="form-overlay"
          onClick={handleOutsideClick}
          className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-75 z-50"
        >
          <div
            onClick={(e) => e.stopPropagation()}
            className="p-10 bg-white rounded-lg max-w-md w-full mx-auto shadow-lg"
          >
            <div className="justify-center text-center text-2xl">
              Upload the Details of Product
            </div>
            <form onSubmit={handleSubmit} className="space-y-6">
              <div>
                <input
                  type="file"
                  name="image"
                  accept="image/*"
                  onChange={(e) =>
                    setFormData({ ...formData, image: e.target.files[0] })
                  }
                  required
                  className="block py-2 mt-2 px-3 w-full text-sm text-gray-900 border border-gray-300 rounded-md cursor-pointer bg-gray-50 focus:outline-none"
                />
              </div>

              <div>
                <input
                  type="text"
                  name="name"
                  placeholder="Enter the product Name"
                  value={formData.name}
                  onChange={handleChange}
                  required
                  className="w-full placeholder-gray-500 px-3 mt-2 py-2 bg-white border border-gray-600 rounded-md text-black placeholder-gray-300"
                />
              </div>

              <div>
                <input
                  type="text"
                  name="type"
                  placeholder="Enter the Type"
                  value={formData.type}
                  onChange={handleChange}
                  required
                  className="w-full px-3 placeholder-gray-500 mt-2 py-2 bg-white border border-gray-600 rounded-md text-black placeholder-gray-300"
                />
              </div>

              <div>
                <textarea
                  name="description"
                  placeholder="Enter the Description"
                  value={formData.description}
                  onChange={handleChange}
                  required
                  className="w-full px-3 placeholder-gray-500 mt-2 py-2 bg-white border border-gray-600 rounded-md text-black placeholder-gray-300"
                />
              </div>

              <div>
                <input
                  type="number"
                  min="0"
                  name="price"
                  placeholder="Selling Price"
                  value={formData.price}
                  onChange={handleChange}
                  required
                  className="w-full px-3 mt-2 placeholder-gray-500 py-2 bg-white border border-gray-600 rounded-md text-black placeholder-gray-300"
                />
              </div>
              <div className="flex justify-center items-center">
                <label htmlFor="bid" className="ml-2 text-sm">
                  Want to Bid
                </label>
                <input
                  type="checkbox"
                  id="bid"
                  checked={bid}
                  onChange={(e) => setBid(e.target.checked)}
                  className="h-4 w-4 ml-4 text-blue-600 border-gray-300 rounded"
                />
              </div>
              <button
                type="submit"
                className="w-full bg-green-500 text-black py-2 px-4 rounded-md hover:bg-green-700 transition duration-200"
              >
                Submit
              </button>
            </form>
          </div>
        </div>
      )}
      <Footer />
    </div>
  );
};

export default Sell;
